package bradesco600

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestCreditAssessment(t *testing.T) {
	segment := "1501202400000000000117304ELETROZEMA S/A                          20264047310001961                   150120241402202400000000000000000000000083-REAL                                                          0000124798515.01.2024001"

	parsed, err := Parse(segment)
	if err != nil {
		assert.Fail(t, err.Error())
	}

	assert.Equal(t, "15012024", parsed.(CreditAssessment).BaseDate.Format("02012006"))
	assert.Equal(t, "ELETROZEMA S/A", parsed.(CreditAssessment).CustomerName)
	assert.Equal(t, "083-REAL", parsed.(CreditAssessment).Indexer)
}
